import React, { Component } from 'react';

import Display from '../components/display';
import Botao from '../components/button';
import './calculator.css';

class Calculator extends Component{
    ClearMemory = () =>{
        console.log('clear');
    }

    SetOPeration = operation =>{
        console.log(operation)
    }

    addDigit = n =>{
        console.log(n);
    }
    render(){
        const addDigit = n => this.addDigit(n);
        const SetOperation = op => this.setOperation(op);

        return(
            <div className="Calculadora">
                <Display value={100}/>
                <Botao label="AC" click={() => { this.ClearMemory()}}/>
                <Botao label="/" click={(op) => SetOperation(op)}/>
                <Botao label="7"/>
                <Botao label="8"/>
                <Botao label="9"/>
                <Botao label="*"/>
                <Botao label="4"/>
                <Botao label="5"/>
                <Botao label="6"/>
                <Botao label="-"/>
                <Botao label="1"/>
                <Botao label="2"/>
                <Botao label="3"/>
                <Botao label="+"/>
                <Botao label="0"/>
                <Botao label="."/>
                <Botao label="="/>
            </div>
        );
    }
}
export default Calculator;
